import React, {useEffect, useState} from "react";
import {Link} from "react-router-dom";
import SideBar from "./SideBar";
import Alerts from "./Alerts";
import ExternalWebsites from "./ExternalWebsites";
import withContext from "../withContext";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

const ViewPosting = props => {

  let posting = localStorage.getItem("posting");
  const theID = props.match.params.id;
  console.log(theID);
  const result = props.context.getPost(theID);
  posting = (result === undefined) ? JSON.parse(posting) : result;
  //let thePics = props.context.getTargetPhotos(theID, posting.numOfPics);
  //console.log(thePics);
  localStorage.setItem("posting", JSON.stringify(posting));
  console.log(posting);

  const [loading, setLoading] = useState(true);
  const [thePics, setPics] = useState([]);
  const [pIndex, setIndex] = useState(0);
  const [openImage, setOpen] = useState(false);
  let carousel;

  useEffect(() => {
    window.scrollTo(0, 0);
    setPics(props.context.getTargetPhotos(theID, posting.numOfPics));
    setTimeout(() => {
      setLoading(false)
   }, 1000);
  }, []);

  const settings = {
    dots: false,
    infinite: true,
    speed: 500,
    lazyLoad: 'progressive',
    autoplay: true,
    autoplaySpeed: 6000,
    slidesToShow: 1,
    slidesToScroll: 1,
    fade: true,
    arrows: false,
    afterChange: current => setIndex(current)
  };

  if (!loading){
    carousel = thePics && thePics.length > 0 ? (
      <Slider
          {...settings}
          >
          {thePics.map((pic, index) => 

            <div className="slick-slide" key={index}>
              <h2 className="slick-slide-title">{posting.title === "" ? "Article View" : posting.title}</h2>
              <img className="slick-slide-image" name={index} onClick={testfunc} alt={posting.id} 
              src={process.env.PUBLIC_URL + `/images/post${posting.id}/${pic}`} />

            </div>
          )}

      </Slider>
      ):(
        <figure  className="image is-4x3">
          <img style={{height:"30rem"}} src={"/images/high_commissioner.jpg" } />
        </figure>
      )


  return (
    <>
      <div className="hero">
        <div className="header">
          <div className="hero blkYellow page-header" >
            <div className="page-header-body container">
              <h4 className="title"> {posting.title} </h4>
            </div>
          </div>
          <br />
          <nav className="breadcrumb is-centered" aria-label="breadcrumbs">
            <ul>
            <li><Link to="/">Home</Link></li>
              <li><Link to="/postings">News Articles and Notices</Link></li>
              <li className="is-active"><a href="#" aria-current="page">{posting.title}</a></li>
            </ul>
          </nav>
        </div>

        <div className="body" style={{color:"yellow"}}>
          <div className="hero">
            <div className="hero-body"> 
              <div className="columns">

                <div className="column">
                  <article className="message special-notice is-content is-size-4">
                      <div>
                        {carousel} 
                      </div> 
                    <div className="message-body">
                      <div className="box grnBkgn">
                        <div className="has-text-left" >
                          By : <b style={{ textTransform: "capitalize" }} >
                                {posting.author}
                              </b>
                        </div>
                        <div className="is-clearfix has-text-left">
                          <span className="tag is-primary">
                              {posting.date ? (
                                <small>{posting.date}</small>
                                ) : (
                                <small className="has-text-warning">No Date Available</small>
                                )}
                            </span>
                        </div>
                      </div>

                      <p className="is-size-5 p-format">
                      {posting.body}
                      </p>

                    </div>
                  </article>

                  <Alerts />

                  <br></br> 

                  <ExternalWebsites />

                </div>

                <div className="column is-one-quarter">
                  <SideBar />

                </div>



              </div>
            </div>
          </div>
        </div>

      </div>
    </>
  )
}}
export default withContext(ViewPosting);
