import React, { Component } from "react";
import { Switch, Route, Link, BrowserRouter as Router } from "react-router-dom";

import HomePage from './components/HomePage';
import CitizenPage from './components/CitizenPage';
import VisitorPage from './components/VisitorPage';
import AffairsPage from './components/AffairsPage';
import AmbassadorPage from './components/AmbassadorPage';
import DocumentsPage from './components/DocumentsPage';
import MinistryPage from './components/MinistryPage';
import ContactPage from './components/ContactPage';
import Login from './components/Login';
import NoticeList from './components/NoticeList';
import PassportPage from './components/VisaPage';
import VisaPage from './components/VisaPage';
import ViewNotice from './components/ViewNotice';
import AddNotice from './components/AddNotice';
import ServicePage from './components/ServicePage';

import Context from "./Context";
import CitizensPage from "./components/CitizenPage";
import VistorPage from "./components/VisitorPage";

import axios from 'axios';
import jwt_decode from 'jwt-decode';

export default class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      user: null,
      notices: []
    };
    this.routerRef = React.createRef();
  }

  componentDidMount() {
    let user = localStorage.getItem("user");
    user = user ? JSON.parse(user) : null;
    this.setState({ user });
  }

  login = async (email, password) => {
    const res = await axios.post(
      'http://localhost:3001/login',
      { email, password },
    ).catch((res) => {
      return { status: 401, message: 'Unauthorized' }
    })

    if(res.status === 200) {
      //const { email } = jwt_decode(res.data.accessToken)
      const user = {
        email,
        token: res.data.accessToken,
        accessLevel: email === 'admin@example.com' ? 0 : 1
      }

      this.setState({ user });
      localStorage.setItem("user", JSON.stringify(user));
      return true;
    } else {
      return false;
    }
  }

  logout = e => {
    e.preventDefault();
    this.setState({ user: null });
    localStorage.removeItem("user");
  };


  render() {
    return (
      <Context.Provider
        value={{
          ...this.state,
          login: this.login
        }}
      >
        <Router ref={this.routerRef}>
        <div className="App">
          <nav
            className="navbar container"
            role="navigation"
            aria-label="main navigation"
          >
            <div className="navbar-brand">
              <b className="navbar-item is-size-4 "><Link to="/" className="navbar-item">
                  JHC Nigeria
                </Link></b>
              <label
                role="button"
                class="navbar-burger burger"
                aria-label="menu"
                aria-expanded="false"
                data-target="navbarBasicExample"
                onClick={e => {
                  e.preventDefault();
                  this.setState({ showMenu: !this.state.showMenu });
                }}
              >
                <span aria-hidden="true"></span>
                <span aria-hidden="true"></span>
                <span aria-hidden="true"></span>
              </label>
            </div>
              <div className={`navbar-menu ${
                  this.state.showMenu ? "is-active" : ""
                }`}>
                <Link to="/citizens" className="navbar-item">
                  Jamaican Nationals
                </Link>
                <Link to="/visitors" className="navbar-item">
                  Visiting Jamaica 
                </Link>
                <Link to="/affairs" className="navbar-item">
                  Consulate Affairs
                </Link>
                <Link to="/services" className="navbar-item">
                  Consulate Services
                </Link>

                {this.state.user && this.state.user.accessLevel < 1 && (
                  <Link to="/add-notice" className="navbar-item">
                    Add Notice
                  </Link>
                )}

                <Link to="/contact" className="navbar-item">
                  Contact
                </Link>
                {!this.state.user ? (
                  <Link to="/login" className="navbar-item">
                    Login
                  </Link>
                ) : (
                  <Link to="/" onClick={this.logout} className="navbar-item">
                    Logout
                  </Link>
                )}
              </div>
            </nav>
            <Switch>
              <Route exact path="/" component={HomePage} />
              <Route exact path="/login" component={Login} />
              <Route exact path="/citizens" component={CitizenPage} />
              <Route exact path="/visitors" component={VistorPage} />
              <Route exact path="/affairs" component={AffairsPage} />
              <Route exact path="/services" component={ServicePage} />
              <Route exact path="/add-notice" component={AddNotice} />
              <Route exact path="/contact" component={ContactPage} />
            </Switch>
          </div>
        </Router>
      </Context.Provider>
    );
  }
}
