import React, { useState } from 'react';
//import { Document, Page } from 'react-pdf';
import { Document, Page } from 'react-pdf/dist/esm/entry.webpack';

const Test = props => {
  const [numPages, setNumPages] = useState(null);
  const [pageNumber, setPageNumber] = useState(1);
  const param = props.match.params.id;
  console.log(params);

  function onDocumentLoadSuccess({ numPages }) {
    setNumPages(numPages);
  }

  const documentList = [
    {
        id : 0,
        src : '/documents/Jamaican-Citizenship-Application-Descent.pdf'
    },
    {
        id : 1,
        src : '/documents/Jamaican-Citizenship-Application-Marriage.pdf'
    }
  ]

  return (
    <div className="hero">
     <strong> <i className="fa fa-file-pdf-o"></i> <a download href={process.env.PUBLIC_URL + documentList[{param}].src}> Download Citizen Application Form - Descent</a> </strong>
      <Document
        file={process.env.PUBLIC_URL + '/documents/Jamaican-Citizenship-Application-Descent.pdf'}
        onLoadSuccess={onDocumentLoadSuccess}
      >
        {Array.from(new Array(numPages), (el, index) => (
            <Page key={`page_${index + 1}`} pageNumber={index + 1} />
        ))}
      </Document>
      <p>Page {pageNumber} of {numPages}</p>
    </div>
  );
}

export default Test;
