import React from "react";

const NoticeItem = props => {
  const { notice } = props;
  return (
    <div className="column is-half">
      <div className="box">
        <div className="media">
          <div className="media-left">
            <figure className="image is-64x64">
              <img
                src="https://bulma.io/images/placeholders/128x128.png"
                alt={notice.description}
              />
            </figure>
          </div>
          <div className="media-content">
            <b style={{ textTransform: "capitalize" }}>
              {notice.title}{" "}
              <span className="tag is-primary">{notice.author}</span>
            </b>
            <div>{notice.description}</div>
            {notice.date ? (
              <small>{notice.date + " New"}</small>
            ) : (
              <small className="has-text-warning">No Date Available</small>
            )}
            <div className="is-clearfix">
              <button className="button is-small is-outlined is-primary   is-pulled-right" >
                button
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default NoticeItem;
