import React from "react";
import {Link} from "react-router-dom";

const PostingItem = props => {
  const { posting } = props;
  return (
    <div className="column is-half">
      <Link to="/">
        <div className="box posting-item-card" style={{height:"100%"}}>
          <div className="has-text-left"> <small style={{color:"red"}}>{posting.type}</small> </div>
          <div className="media">

            <div className="media-left">
              <figure className="image is-64x64">
                <img
                  src="https://bulma.io/images/placeholders/128x128.png"
                  alt={posting.description}
                />
              </figure>
            </div>
            <div className="media-content">
              <b style={{ textTransform: "capitalize",color:"blue" }}>
                {posting.title}{" "}
              </b>
              <br/>
              <div className="has-text-left" >
              By : <b style={{ textTransform: "capitalize" }} >
                    {posting.author}
                  </b>
              </div>
              <div className="is-clearfix has-text-right">
                <span className="tag is-primary">
                    {posting.date ? (
                      <small>{posting.date + " New"}</small>
                      ) : (
                      <small className="has-text-warning">No Date Available</small>
                  )}
                  </span>
              </div>
              <div>{posting.description}</div>

            </div>
          </div>
        </div>
      </Link>
    </div>
  );
};

export default PostingItem;
