import React, { Component } from "react";
import { Switch, Route, Link, BrowserRouter as Router } from "react-router-dom";
import './App.css';
import HomePage from './components/HomePage';
import CitizenPage from './components/CitizenPage';
import VisitorPage from './components/VisitorPage';
import AboutPage from './components/AboutPage';
import CommissionerPage from './components/CommissionerPage';
import DocumentsPage from './components/DocumentsPage';
import MinistryPage from './components/MinistryPage';
import ContactPage from './components/ContactPage';
import Login from './components/Login';
import PostingList from './components/PostingList';
import PassportPage from './components/VisaPage';
import VisaPage from './components/VisaPage';
import ViewPosting from './components/ViewPosting';
import AddPosting from './components/AddPosting';
import ServicePage from './components/ServicePage';
import GeneralInfo from './components/GeneralInfo';

import Footer from './components/Footer';
import Banner from './components/Banner';
import Context from "./Context";

import axios from 'axios';
import jwt_decode from 'jwt-decode';


export default class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      user: null,
      postings: [],
      contactName:"", contactEmail: "", nationality: "", contactSubject:"", contactMessage:""
    };
    this.routerRef = React.createRef();
  }



  async componentDidMount() {
    let user = localStorage.getItem("user");
    const postings = await axios.get('http://localhost:3001/postings');
    user = user ? JSON.parse(user) : null;
    this.setState({ user, postings : postings.data });
  }

  componentDidUpdate(){
    window.scrollTo(0, 0);
  }

  addPosting = (posting, callback) => {
    let postings = this.state.postings.slice();
    postings.push(posting);
    this.setState({ postings }, () => callback && callback());
  };

  login = async (email, password) => {
    const res = await axios.post(
      'http://localhost:3001/login',
      { email, password },
    ).catch((res) => {
      return { status: 401, message: 'Unauthorized' }
    })

    if(res.status === 200) {
      const { email } = jwt_decode(res.data.accessToken)
      const user = {
        email,
        token: res.data.accessToken,
        accessLevel: email === 'admin@example.com' ? 0 : 1
      }

      this.setState({ user });
      localStorage.setItem("user", JSON.stringify(user));
      return true;
    } else {
      return false;
    }
  }

  logout = e => {
    e.preventDefault();
    this.setState({ user: null });
    localStorage.removeItem("user");
  };


  render() {
    return (
      <Context.Provider
        value={{
          ...this.state,
          login: this.login,
          addPosting:this.addPosting
        }}
      >
        <Router ref={this.routerRef}>
        <div className="App">
          <nav
            className="navbar"
            role="navigation"
            aria-label="main navigation"
          >
            <div className="navbar-brand">
              <b className="navbar-item is-expanded is-tab is-size-4 "><Link onClick={ () => {this.setState({ showMenu: !this.state.showMenu })}} to="/" className="navbar-item">
