import React, {useEffect} from "react";
import {Link} from "react-router-dom";

const PostingItem = props => {

  useEffect(() => {
    window.scrollTo(0, 0)
  }, []);

  const { posting } = props;
  return (
    <div className="column news-article">
      <Link to={`/view-posting/${posting.id}`}>
        <div className="box posting-item-card is-half" style={{height:"100%"}}>
          <div className="has-text-left"> <small style={{color:"red"}}>{posting.type}</small> </div>
          <div className="columns">

            <div className="column is-one-fifth">
              <figure className="image is-64x64">
                <img
                  src="https://bulma.io/images/placeholders/128x128.png"
                  alt={posting.description}
                />
              </figure>
            </div>
            <div className="column">
              <b className="posting-title" style={{ textTransform: "capitalize"}}>
                {posting.title}{" "}
              </b>
              <br/>
              <div className="has-text-left" >
              By : <b className="posting-author" style={{ textTransform: "capitalize" }} >
                    {posting.author}
                  </b>
              </div>
              <div className="is-clearfix has-text-right">
                <span className="tag is-primary">
                    {posting.date ? (
                      <small>{posting.date}</small>
                      ) : (
                      <small className="has-text-warning">No Date Available</small>
                  )}
                  </span>
              </div>
              <div>{posting.description}</div>

            </div>
          </div>
        </div>
      </Link>
    </div>
  );
};

export default PostingItem;
